import React, { Component } from 'react';
import './Entities.css';
import store from '../../store';
import Entity from '../entity/Entity';

var products = [
    {id:0, name: 'Aretes piedras', description: '100% trabajo artesanal heho en el Huila', category:'001', price: 100, creationDate: '', quantity: 0, mainPic: "data:image/jpeg;base64," },
    {id:2, name: 'Aretes perlas', description: '100% trabajo artesanal heho en el departamento del Cauca', category:'001', price: 100, creationDate: '', quantity: 0, mainPic: "http://daramoda.com/wp-content/uploads/2014/10/A84.3-300x300.jpg" },
    {id:3, name: 'Souvenir XYZ', description: '100% trabajo artesanal heho en San Andrés Islas', category:'002', price: 100, creationDate: '', quantity: 0, mainPic: "" },
    {id:4, name: 'Brazalete oro blanco', description: 'Trabajo artesanal heho en el Cauca, 100% Colombiano', category:'003', price: 100, creationDate: '', quantity: 0, mainPic: "data:image/jpeg;base64," },
    {id:5, name: 'Collar de perlas', description: 'Trabajo artesanal de excelente calidad', category:'004', price: 100, creationDate: '', quantity: 0, mainPic: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTHzuAFu4R-BSysvkzY9WtX9Wj_P3JEeKBkM0cvaNwtxyju3GKn" },
    {id:6, name: 'Aretes animales', description: 'Súper promo', category:'001', price: 100, creationDate: '', quantity: 0, mainPic: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRhPgfpVBxcXzAMT53pLRrQ_dLF-GuQfzLteg8aHAIQqYQVhB-h3Q" },
    {id:7, name: 'Brazalete grabado', description: 'Oferta única', category:'003', price: 100, creationDate: '', quantity: 0, mainPic: "data:image/jpeg;base64," },
];

class Entities extends Component {
    constructor(props) {
        super();
        this.state = {
            searchText: '',
            searchResults: []
        }

        store.subscribe(() => {
            //this.setState({username: store.getState().user1});
        });
    }
    componentDidUpdate(){ }
    componentDidMount(){

    }
    componentWillReceiveProps(nextProps){ }
    componentWillUpdate(nextProps, nextState){ }
    componentDidUpdate(prevProps, prevState){ }
    setSearchText(e){
        this.setState({searchText:e.target.value});
    }
    handleKeyPress(e){
        if(e.key === 'Enter'){
            this.applySearch();
        }
    }
    applySearch(){
        var self = this;
        var results = [];
        if(self.state.searchText){
            results = products.filter(function(ele, index){
                return ele.name.toLocaleLowerCase().indexOf(self.state.searchText.toLocaleLowerCase()) !== -1;
            });
        }
        this.setState({searchResults: results});
    }
  render() {
      return (
          <div className="container-fluid">
              <div className="entities__search-container col-sm-12">
                  <div className="col-sm-4"></div>
                  <div className="entities__search-bar col-sm-4">
                      <i className="fa fa-search"></i>
                      <input type="text"
                             id="search-text-input"
                             placeholder="Type to search..."
                             value={this.state.searchText}
                             onChange={this.setSearchText.bind(this)}
                             onKeyPress={this.handleKeyPress.bind(this)}
                      />
                      <button type="button"
                        className="btn btn-primary"
                        onClick={this.applySearch.bind(this)}><i className="fa fa-search"></i></button>
                  </div>
                  <div className="col-sm-4"></div>
              </div>
              { this.state.searchResults.map(function(product) {
                  return <Entity  key={product.id} data={product}></Entity>;
              }.bind(this)) }
          </div>);
  }
}

export default Entities;
